"""
Dedicated test for auto-CCS pipeline - kept separate from test_ccs because the
outputs are very different from the vanilla CCS pipeline
"""

from pysiv2.custom.base import TestReportStatistics


class TestCCSAuto(TestReportStatistics):
    """
    Test output of the CCS report.
    """
    REPORT_ID = "ccs2"
    TEST_ID = "ccs2"
    METRIC_IDS = [
        "total_number_of_ccs_bases",
        "mean_ccs_readlength",
        "number_of_ccs_reads",
    ]
